import React, { useState, useEffect} from 'react'
import { uuid } from 'uuidv4'

import { AiFillCheckCircle, AiFillDelete } from 'react-icons/ai'
import { BsArrowCounterclockwise } from 'react-icons/bs'

import './style.css'

interface Todo{
    id: string,
    message: string,
    done: boolean
}

const TodoList: React.FC = () => {
    const mock = [
        {
            id: uuid(),
            message: "Teste 1",
            done: false
        },
        {
            id: uuid(),
            message: "Teste 2",
            done: false
        }
    ]

    const [todoList, setTodoList] = useState<Todo[]>([])
    const [todo, setTodo] = useState("")

    useEffect(() => {
        const todos = localStorage.getItem("todoList")
        if(todos){
            setTodoList(JSON.parse(todos))
        }
    }, [])

    useEffect(() => {
        localStorage.setItem('todoList', JSON.stringify(todoList))
    }, [todoList])

    function addTodo(){
        const newTodo = {
            id: uuid(),
            message: todo,
            done: false
        }

        setTodoList(
            [newTodo, ...todoList]
        )
    }

    function deleteTodo(id: string){
        const filteredData = todoList.filter(item => item.id !== id)
        setTodoList(filteredData)
    }

    function changeStatus(id: String){
        const oldTodo = todoList.filter(item => item.id == id)[0]
        const filteredData = todoList.filter(item => item.id !== id)
        const newTodo = {
            id: oldTodo.id,
            message: oldTodo.message,
            done: !oldTodo.done
        }
        setTodoList(
            [newTodo, ...filteredData]
        )

    }

    return(
        <div id="todoList">
            <h1>TodoList</h1>

            <div className="todoInput">
                <input type="text" value={todo} onChange={e=>setTodo(e.target.value)} />
                <button type="button" 
                className="addTodo"
                onClick={addTodo}> Add</button>
            </div>

            <div>
                {
                    todoList.map(todo => (
                        <div className="todo" key={todo.id}> 
                            <div>{todo.message}</div>
                            <div className="actions">
                                {
                                    !todo.done?(
                                        <button onClick={() => changeStatus(todo.id)} type="button" className="icon">
                                        <AiFillCheckCircle color="#27ae60" />
                                        </button>
                                    ):(
                                        <button onClick={() => changeStatus(todo.id)} type="button" className="icon">
                                        <BsArrowCounterclockwise color= "#e67e22" />
                                        </button>
                                    )
                                }


                                <button onClick={() => deleteTodo(todo.id)} type="button" className="icon">
                                <AiFillDelete color="#e74c3c" />
                                </button>

                            </div>
                        </div>
                    ))
                }
            </div>

        </div>
    )
}

export default TodoList
